import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Make sure to import the CSS file for custom styles

function App() {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState({
    name: '',
    content: '',
    startDate: '',
    endDate: '',
    tags: '',
    status: '',
    activityId: ''
  });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    const { data } = await axios.get('http://localhost:5001/todos');
    setTodos(data);
  };

  const handleChange = (e) => {
    setTask({ ...task, [e.target.name]: e.target.value });
  };

  const addTodo = async () => {
    const newTask = {
      ...task,
      tags: task.tags.split(',').map(tag => tag.trim()),  // Convert string to array
    };
    const { data } = await axios.post('http://localhost:5001/todos', newTask);
    setTodos([...todos, data]);
    setTask({
      name: '',
      content: '',
      startDate: '',
      endDate: '',
      tags: '',
      status: '',
      activityId: ''
    });  // Reset the form
  };

  const editTodo = (todo) => {
    setEditId(todo._id);
    setTask({ ...todo, tags: todo.tags.join(', ') }); // Convert array to string
  };

  const saveTodo = async () => {
    const updatedTask = {
      ...task,
      tags: task.tags.split(',').map(tag => tag.trim()),  // Convert string back to array
    };
    const { data } = await axios.put(`http://localhost:5001/todos/${editId}`, updatedTask);
    const updatedTodos = todos.map(todo => todo._id === editId ? data : todo);
    setTodos(updatedTodos);
    setEditId(null);
    setTask({
      name: '',
      content: '',
      startDate: '',
      endDate: '',
      tags: '',
      status: '',
      activityId: ''
    });  // Reset form
  };

  const deleteTodo = async (id) => {
    await axios.delete(`http://localhost:5001/todos/${id}`);
    setTodos(todos.filter(todo => todo._id !== id));
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">ToDo List</h2>
      <div className="form-group">
        <input className="form-control" name="name" value={task.name} onChange={handleChange} placeholder="Task Name" />
        <textarea className="form-control mt-2" name="content" value={task.content} onChange={handleChange} placeholder="Task Content" />
        <input type="date" className="form-control mt-2" name="startDate" value={task.startDate} onChange={handleChange} placeholder="Start Date" />
        <input type="date" className="form-control mt-2" name="endDate" value={task.endDate} onChange={handleChange} placeholder="End Date" />
        <input className="form-control mt-2" name="tags" value={task.tags} onChange={handleChange} placeholder="Tags (comma-separated)" />
        <input className="form-control mt-2" name="status" value={task.status} onChange={handleChange} placeholder="Status" />
        <input className="form-control mt-2" name="activityId" value={task.activityId} onChange={handleChange} placeholder="Activity ID" />
        {editId ? (
          <button className="btn btn-success mt-3" onClick={saveTodo}>Save Changes</button>
        ) : (
          <button className="btn btn-outline-secondary mt-3" onClick={addTodo}>Add Task</button>
        )}
      </div>
      <ul className="list-group">
        {todos.map(todo => (
          <li key={todo._id} className="list-group-item">
            <div>Name: {todo.name}</div>
            <div>Content: {todo.content}</div>
            <div>Start Date: {new Date(todo.startDate).toLocaleDateString()}</div>
            <div>End Date: {new Date(todo.endDate).toLocaleDateString()}</div>
            <div>Tags: {todo.tags.join(', ')}</div>
            <div>Status: {todo.status}</div>
            <div>Activity ID: {todo.activityId}</div>
            <button className="btn btn-info btn-sm" onClick={() => editTodo(todo)}>Edit</button>
            <button className="btn btn-danger btn-sm" onClick={() => deleteTodo(todo._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
