import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Home() {
    const [tasks, setTasks] = useState([]);

    useEffect(() => {
        const fetchTasks = async () => {
          try {
            const response = await axios.get('http://localhost:5001/api/tasks/todos');
            if (response.data.length === 0) {
              setTasks(null); // Set tasks to null if no tasks are fetched
            } else {
              setTasks(response.data);
            }
          } catch (error) {
            console.error('Failed to fetch tasks:', error);
            setTasks(null); // Handle error or no data by setting tasks to null
          }
        };
        fetchTasks();
      }, []);

    return (
        <div className="container mt-5">
            <h2>Welcome to the Home Page</h2>
            <div className="notifications">
                <h3>Notifications</h3>
                <p>There are no notifications.</p>
            </div>
            <div className="latest-tasks">
                <h3>Latest Tasks</h3>
                {tasks === null ? (
                    <p>No tasks available.</p>
                ) : (
                    <ul>
                        {tasks.map(task => (
                            <li key={task._id}>{task.name} - Due: {new Date(task.endDate).toLocaleDateString()}</li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
}

export default Home;
