import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css'; // Make sure to import the CSS file for custom styles

function App() {
  const [todos, setTodos] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [task, setTask] = useState({
    name: '',
    content: '',
    startDate: '',
    endDate: '',
    tags: '',
    status: '',
    activityId: ''
  });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchTodos();
    fetchStatuses();
  }, []);

  const fetchStatuses = async () => {
    const { data } = await axios.get('http://localhost:5001/statuses');
    setStatuses(data);
    if (data.length > 0) {
      setTask(prev => ({ ...prev, status: data[0].title })); // Set default status
    }
  };

  const fetchTodos = async () => {
    const { data } = await axios.get('http://localhost:5001/todos');
    setTodos(data);
  };

  const handleChange = (e) => {
    setTask({ ...task, [e.target.name]: e.target.value });
  };

  const addTodo = async () => {
    const newTask = {
      ...task,
      tags: task.tags.split(',').map(tag => tag.trim()),  // Convert string to array
    };
    const { data } = await axios.post('http://localhost:5001/todos', newTask);
    setTodos([...todos, data]);
    setTask({
      name: '',
      content: '',
      startDate: '',
      endDate: '',
      tags: '',
      status: '',
      activityId: ''
    });  // Reset the form
  };

  const editTodo = (todo) => {
    setEditId(todo._id);
    setTask({ ...todo, tags: todo.tags.join(', ') }); // Convert array to string
  };

  const saveTodo = async () => {
    const updatedTask = {
      ...task,
      tags: task.tags.split(',').map(tag => tag.trim()),  // Convert string back to array
    };
    const { data } = await axios.put(`http://localhost:5001/todos/${editId}`, updatedTask);
    const updatedTodos = todos.map(todo => todo._id === editId ? data : todo);
    setTodos(updatedTodos);
    setEditId(null);
    setTask({
      name: '',
      content: '',
      startDate: '',
      endDate: '',
      tags: '',
      status: '',
      activityId: ''
    });  // Reset form
  };

  const deleteTodo = async (id) => {
    await axios.delete(`http://localhost:5001/todos/${id}`);
    setTodos(todos.filter(todo => todo._id !== id));
  };

  return (
    <div>
      <Router>
      <div>
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link className="navbar-brand" to="/">ToDo App</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
            <div className="navbar-nav">
              <Link className="nav-link" to="/">Home</Link>
              <Link className="nav-link" to="/tasks">Tasks</Link>
            </div>
          </div>
        </nav>

        <Routes>
          <Route path="/tasks" element={<Tasks />} />
          <Route path="/" element={
            <div className="container mt-5">
              <h2>Welcome to the Home Page</h2>
              {/* Home page content */}
            </div>
          } />
        </Routes>
      </div>
    </Router>


    <div className="container mt-5">
      <h2 className="text-center mb-4">ToDo List</h2>
      <div className="form-group">
        <input type="text" className="form-control mb-2" name="name" value={task.name} onChange={handleChange} placeholder="Task Name" />
        <textarea className="form-control mb-2" name="content" value={task.content} onChange={handleChange} placeholder="Task Content" />
        <input type="date" className="form-control mb-2" name="startDate" value={task.startDate} onChange={handleChange} placeholder="Start Date" />
        <input type="date" className="form-control mb-2" name="endDate" value={task.endDate} onChange={handleChange} placeholder="End Date" />
        <input type="text" className="form-control mb-2" name="tags" value={task.tags} onChange={handleChange} placeholder="Tags (comma-separated)" />
        <select className="form-control mb-2" name="status" value={task.status} onChange={handleChange}>
          {statuses.map(status => (
            <option key={status._id} value={status.title}>{status.title}</option>
          ))}
        </select>        <input type="number" className="form-control mb-2" name="activityId" value={task.activityId} onChange={handleChange} placeholder="Activity ID" />
        <button className="btn btn-outline-secondary" onClick={addTodo}>Add Task</button>
      </div>
      <div className="row">
        {todos.map(todo => (
          <div key={todo._id} className="col-md-4 mb-4">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">{todo.name}</h5>
                <h6 className="card-subtitle mb-2 text-muted">{todo.status}</h6>
                <p className="card-text">{todo.content}</p>
                <div className="card-footer">
                  <small className="text-muted">Start: {new Date(todo.startDate).toLocaleDateString()} - End: {new Date(todo.endDate).toLocaleDateString()}</small>
                </div>
                {editId === todo._id ? (
                  <>
                    <button className="btn btn-success btn-sm" onClick={saveTodo}>Save</button>
                    <button className="btn btn-secondary btn-sm" onClick={() => setEditId(null)}>Cancel</button>
                  </>
                ) : (
                  <>
                    <button className="btn btn-primary btn-sm" onClick={() => editTodo(todo)}>Edit</button>
                    <button className="btn btn-danger btn-sm" onClick={() => deleteTodo(todo._id)}>Delete</button>
                  </>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
    </div>

  );
}

export default App;
