import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Home() {
    const [tasks, setTasks] = useState([]);
    const [notifications, setNotifications] = useState([]);

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const response = await axios.get('http://localhost:5001/api/tasks');
                setTasks(response.data);
                // Check each task to see if it's overdue and notify
                response.data.forEach(task => {
                    if (new Date(task.endDate) < new Date()) {
                        setNotifications(prev => [...prev, `Task "${task.name}" is overdue!`]);
                    }
                });
            } catch (error) {
                console.error('Failed to fetch tasks:', error);
                setNotifications(prev => [...prev, "Failed to fetch tasks."]);
            }
        };
        fetchTasks();
    }, []);

    return (
        <div className="container mt-5">
            <h2>Welcome to the Home Page</h2>
            <div className="notifications">
                <h3>Notifications</h3>
                {notifications.length > 0 ? (
                    notifications.map((notification, index) => (
                        <p key={index} className="notification">{notification}</p>
                    ))

                ) : (
                    <p>No notifications.</p>
                )}
            </div>
            <div className="latest-tasks">
                <h3>Latest Tasks</h3>
                {tasks.length > 0 ? (
                    <ul>
                        {tasks.map(task => (
                            <li key={task._id}>{task.name} - Due: {new Date(task.endDate).toLocaleDateString()}</li>
                        ))}
                    </ul>
                ) : (
                    <p>No tasks available.</p>
                )}
            </div>
        </div>
    );
}

export default Home;
