import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Ensure this CSS file exists and is correctly imported

function Home() {
    const [tasks, setTasks] = useState([]); // Initialize state for storing tasks

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const response = await axios.get('http://localhost:5001/api/tasks');
                setTasks(response.data.slice(0, 5)); // Store the first 5 tasks
            } catch (error) {
                console.error('Failed to fetch tasks:', error);
            }
        };
        fetchTasks();
    }, []);

    return (
        <div className="container mt-5">
            <h2>Welcome to the Home Page</h2>
            <div className="notifications">
                <h3>Notifications</h3>
                <p>There are no notifications.</p>
            </div>
            <div className="latest-tasks">
                <h3>Latest Tasks</h3>
                <ul>
                    {tasks.map(task => (
                        <li key={task.id}>{task.name} - Due: {new Date(task.dueDate).toLocaleDateString()}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Home;
