        const fetchTasks = async () => {
          try {
            const response = await axios.get('http://localhost:5001/api/tasks');
            setTasks(response.data.slice(0, 5)); // Adjust as needed
          } catch (error) {
            console.error('Failed to fetch tasks:', error);
          }
        };
        fetchTasks();
      }, []);
