// src/components/Activities.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Activities() {
  const [activities, setActivities] = useState([]);
  const [newActivity, setNewActivity] = useState("");

  useEffect(() => {
    fetchActivities();
  }, []);

  const fetchActivities = async () => {
    const result = await axios.get('http://localhost:5001/activities');
    setActivities(result.data);
  };

  const addActivity = async () => {
    const result = await axios.post('http://localhost:5001/activities', { title: newActivity });
    setActivities([...activities, result.data]);
    setNewActivity("");  // Reset the input after adding
  };

  return (
    <div className="container">
      <h2>Manage Activities</h2>
      <input 
        type="text"
        value={newActivity}
        onChange={(e) => setNewActivity(e.target.value)}
        placeholder="New Activity Title"
      />
      <button onClick={addActivity}>Add Activity</button>
      <ul>
        {activities.map(activity => (
          <li key={activity._id}>{activity.title}</li>
        ))}
      </ul>
    </div>
  );
}

export default Activities;
