import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState('');
  const [editDueDate, setEditDueDate] = useState('');

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    const { data } = await axios.get('http://localhost:5001/todos');
    setTodos(data);
  };

  const addTodo = async () => {
    const { data } = await axios.post('http://localhost:5001/todos', {
      text: input,
      completed: false,
      dueDate: dueDate
    });
    setTodos([...todos, data]);
    setInput('');
    setDueDate('');
  };

  const toggleCompletion = async (id, completed) => {
    const { data } = await axios.put(`http://localhost:5001/todos/${id}`, {
      completed: !completed
    });
    setTodos(todos.map(todo => (todo._id === id ? data : todo)));
  };

  const startEdit = (todo) => {
    setEditId(todo._id);
    setEditText(todo.text);
    setEditDueDate(new Date(todo.dueDate).toISOString().slice(0, 10));
  };

  const saveEdit = async () => {
    const { data } = await axios.put(`http://localhost:5001/todos/edit/${editId}`, {
      text: editText,
      dueDate: editDueDate
    });
    const updatedTodos = todos.map(todo => todo._id === editId ? data : todo);
    setTodos(updatedTodos);
    cancelEdit();
  };

  const cancelEdit = () => {
    setEditId(null);
    setEditText('');
    setEditDueDate('');
  };

  return (
    <div>
      <input value={input} onChange={e => setInput(e.target.value)} placeholder="Add a new task" />
      <input type="date" value={dueDate} onChange={e => setDueDate(e.target.value)} />
      <button onClick={addTodo}>Add Todo</button>
      <ul>
        {todos.map(todo => (
          <li key={todo._id}>
            {todo._id === editId ? (
              <>
                <input value={editText} onChange={e => setEditText(e.target.value)} />
                <input type="date" value={editDueDate} onChange={e => setEditDueDate(e.target.value)} />
                <button onClick={saveEdit}>Save</button>
                <button onClick={cancelEdit}>Cancel</button>
              </>
            ) : (
              <>
                {todo.text} - Due: {new Date(todo.dueDate).toLocaleDateString()}
                {todo.completed ? " (Completed)" : ""}
                <button onClick={() => toggleCompletion(todo._id, todo.completed)}>
                  Mark as {todo.completed ? "Incomplete" : "Completed"}
                </button>
                <button onClick={() => startEdit(todo)}>Edit</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
