import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Make sure to import the CSS file for custom styles

function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState('');
  const [editDueDate, setEditDueDate] = useState('');

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    const { data } = await axios.get('http://localhost:5001/todos');
    setTodos(data);
  };

  const addTodo = async () => {
    const { data } = await axios.post('http://localhost:5001/todos', {
      text: input,
      completed: false,
      dueDate: dueDate
    });
    setTodos([...todos, data]);
    setInput('');
    setDueDate('');
  };

  const toggleCompletion = async (id, completed) => {
    const { data } = await axios.put(`http://localhost:5001/todos/${id}`, {
      completed: !completed
    });
    setTodos(todos.map(todo => (todo._id === id ? data : todo)));
  };

  const startEdit = (todo) => {
    setEditId(todo._id);
    setEditText(todo.text);
    setEditDueDate(new Date(todo.dueDate).toISOString().slice(0, 10));
  };

  const saveEdit = async () => {
    const { data } = await axios.put(`http://localhost:5001/todos/edit/${editId}`, {
      text: editText,
      dueDate: editDueDate
    });
    const updatedTodos = todos.map(todo => todo._id === editId ? data : todo);
    setTodos(updatedTodos);
    cancelEdit();
  };

  const cancelEdit = () => {
    setEditId(null);
    setEditText('');
    setEditDueDate('');
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">ToDo List</h2>
      <div className="input-group mb-3">
        <input type="text" className="form-control" placeholder="Add a new task" value={input} onChange={e => setInput(e.target.value)} />
        <input type="date" className="form-control" value={dueDate} onChange={e => setDueDate(e.target.value)} />
        <div className="input-group-append">
          <button className="btn btn-outline-secondary" onClick={addTodo}>Add Todo</button>
        </div>
      </div>
      <ul className="list-group">
        {todos.map(todo => (
          <li key={todo._id} className="list-group-item">
            {todo._id === editId ? (
              <div className="input-group">
                <input type="text" className="form-control" value={editText} onChange={e => setEditText(e.target.value)} />
                <input type="date" className="form-control" value={editDueDate} onChange={e => setEditDueDate(e.target.value)} />
                <div className="input-group-append">
                  <button className="btn btn-success" onClick={saveEdit}>Save</button>
                  <button className="btn btn-danger" onClick={cancelEdit}>Cancel</button>
                </div>
              </div>
            ) : (
              <>
                <span className={todo.completed ? "text-success" : ""}>{todo.text} - Due: {new Date(todo.dueDate).toLocaleDateString()}</span>
                <button className="btn btn-secondary btn-sm float-right" onClick={() => startEdit(todo)}>Edit</button>
                <button className="btn btn-info btn-sm float-right mr-2" onClick={() => toggleCompletion(todo._id, todo.completed)}>
                  {todo.completed ? "Mark Incomplete" : "Mark Completed"}
                </button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
